import React, { Component } from "react";
import Card from "./CardUI";
import img1 from "../assets/Breathe.png";
import img2 from "../assets/Jumping.png";
import img3 from "../assets/Happy.png";

/*
class Cards extends Component {
  render() {
    return (
      <div className="container-fluid d-flex justify-content-center">
        <div className="row">
          <div className="col-md-4">
            <Card
              imgsrc={img1}
              title="Expense Tracker"
              text="Need to balance your budget?"
            />
          </div>
          <div className="col-md-4">
            <Card
              imgsrc={img2}
              title="My Story"
              text="Learn about my background"
            />
          </div>
          <div className="col-md-4">
            <Card
              imgsrc={img3}
              title="On the Cloud"
              text="Apps hosted on AWS"
            />
          </div>
        </div>
      </div>
    );
  }
}


export default Cards;

*/

const Cards = ({ cards }) => {
  const cardsArray = cards.map(card => (
    <Card imgsrc={card.name} title={card.title} text={card.text} />
  ));

  return (
    <div className="container-fluid d-flex justify-content-center">
      <div className="row">
        <div className="col-md-4">{cardsArray}</div>
      </div>
    </div>
  );
};

CardList.propTypes = {
  cards: React.PropTypes.array.isRequired
};
