import React from "react";

function Card(props) {
  return (
    <div className="col-md-4">
      <img src={props.card.imgsrc} alt="" />
      <h3>{props.card.title}</h3>
      <p> {props.card.text}</p>
    </div>
  );
}

export default Card;
